mapper.select:
SELECT CHATROOM_ID, PJ_SIMG, PJ_TITLE, PJ_EMAIL, NAME, EMAIL_STARTER, EMAIL, MSG_CONTENT, TO_CHAR(MSG_DATE, 'YYYY/MM/DD HH24:MI:SS') MSG_DATE FROM (SELECT CHATROOM_ID, PJ_SIMG, PJ_TITLE, P.EMAIL AS PJ_EMAIL, EMAIL_STARTER, MSG.EMAIL, M.NAME, MSG_CONTENT, MSG_DATE, MSG_CHECK, ROW_NUMBER() OVER(PARTITION BY CHATROOM_ID ORDER BY MSG_DATE DESC) AS RECENT_CH FROM TUM_PROJECT P, TUM_MEMBER M, TUM_MESSAGE MSG WHERE MSG.PJ_ID = P.PJ_ID AND P.EMAIL = M.EMAIL) WHERE RECENT_CH = 1
AND EMAIL_STARTER = #{email}
AND PJ_EMAIL = #{email}
AND MSG_CHECK = 'N' AND EMAIL NOT IN(#{email})
and chatroom_id in (select distinct(chatroom_id) from tum_message where email in(#{email}))
ORDER BY MSG_DATE ASC
