mapper.select:
 
	select * from(select rownum rno, pj_id, email, pj_title, pj_summary, pj_addr, pj_img, pj_simg, pj_price, pj_end_date-sysdate pj_extra_date, total_price, pj_check_yn, to_char(sysdate, 'yyyy-mm-dd') today_date, to_char(pj_end_date,'yyyy-mm-dd') pj_end_date  
	from (select  t.pj_id, email, pj_title, pj_summary, pj_addr, pj_img, pj_simg, pj_price, pj_end_date, pj_start_date, total_price, pj_check_yn from tum_project t, 
	(select t.pj_id, sum(total_funding_price) total_price from  tum_funding f, tum_project t where t.pj_id = f.pj_id group by t.pj_id) f 
	where t.pj_id=f.pj_id(+) order by t.pj_id desc) where email=#{email}) where rno between #{start} and #{end}
