mapper.update:
UPDATE tum_member
SET CERTIFICATE='y'
WHERE email=#{email} and signkey=#{signkey}

mapper.select:
 
		
		    select count(*) result, name ,email, profile_simg , profile_img from tum_member 
		    where email=#{email} and pass=#{pass} 
		    group by name ,email, profile_simg , profile_img

mapper.update:
	UPDATE tum_member
	SET pass=#{pass}, name=#{name},intro=#{intro},website=#{website},phone=#{phone},profile_img=#{profile_img},profile_simg=#{profile_img} 
	where email=#{email}
